<script>
import { get } from "svelte/store";


	let name = 'world';
	
	//On load
	let answerData = submitAnswer();

	function submitAnswer() {
		let answerData = {
			image: 'king_of_rap',
			imageData: 'data:image/jpeg;base64,',
			message: 'Right answer!'
		}
		return answerData;
	}

	function getQuestion() {
		let questionData = {
			question: 'Who is the king of rap?',
			options: [
				{ name: 'Justin Beiber'},
				{ name: 'Miley Montana'},
				{ name: 'Eminem'},
				{ name: '49cent'}
			]
		}
		return questionData;
	}

	function handleSubmit() {
		submitAnswer();
		isQuestion = !isQuestion;
	}

	function handleNext() {
		getQuestion();
		isQuestion = !isQuestion;
	}

	let questionData = getQuestion();
	let isQuestion = true;
	let optionSelected = 1;
</script>


<div id="top-bar">The king of <i>x</i> quiz</div>

{#if isQuestion}
	<div id="question"> {questionData.question} </div>
	<div id="options">
		{#each questionData.options as { name }}
			<label>
				<input type=radio bind:group={optionSelected} name="optionSelected" value={name}>
				{name}
			</label>
		{/each}
	</div>
	<div id="feedback">
		Feedback/rant:<br>
		<textarea value=""></textarea>
	</div>
	<button preventDefault id="submit-btn" on:click={() => {handleSubmit()}}>Submit!</button>
{:else}
	<div id="king-image__title">This is the {answerData.image}!</div>
	<div id="king-image">
		<img src={answerData.imageData} alt={answerData.image}/>
	</div>
	<div id="message">{answerData.message}</div>
	<button preventDefault id="next-btn" on:click={() => {handleNext()}}>
		Next question
	</button>
{/if}






<div id="footer">
	Made with ❤️ and 🤦 · <a href="www.github.com">View on GitHub</a>
</div>
